/* Function  structure for function  `fnRep_BuscarPermisos` */

/*!50003 DROP FUNCTION IF EXISTS `fnRep_BuscarPermisos` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `fnRep_BuscarPermisos`(pDocumentoDNI char(12),pIdFechaAsist date) RETURNS text CHARSET latin1
BEGIN
	
	
  DECLARE done INT DEFAULT 0;
  DECLARE pHoraInicio time;
  DECLARE pHoraFin TIME;
  DECLARE pPermisos TEXT;
  DECLARE cur1 CURSOR FOR SELECT HoraInicio,HoraFin FROM tareg_registropermisopersonal
  where DocumentoDNI = pDocumentoDNI and FechaPermiso = pIdFechaAsist;
  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;
  
  	
  	
  OPEN cur1;
	
	
  set pPermisos = 'PERMISO\r\n';
  REPEAT
    FETCH cur1 INTO pHoraInicio, pHoraFin;
    IF NOT done THEN
       set pPermisos = concat(pPermisos,pHoraInicio,' E\r\n',pHoraFin,' S\r\n',SUBTIME(pHoraFin,pHoraInicio),' T\r\n');
    END IF;
  UNTIL done END REPEAT;
  CLOSE cur1;
	
  return pPermisos;
  
  END */$$
DELIMITER ;

/* Function  structure for function  `fnRep_RecuperarTurnoLibre` */

/*!50003 DROP FUNCTION IF EXISTS `fnRep_RecuperarTurnoLibre` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `fnRep_RecuperarTurnoLibre`(pDocumentoDNI char(12),pIdFechaAsist date) RETURNS char(250) CHARSET latin1
BEGIN
	
	
	  declare DiaSemana int(2);
	  declare Mensaje char(250);
	  declare inpTipoHorario int(11);
	  declare pTipoTurno int(11);
	  
	  select IdTipoHorario into inpTipoHorario
	  from tapri_contratopersonal 
	  where DocumentoDNI = pDocumentoDNI
	  limit 1;
	  
	  set DiaSemana = DAYOFWEEK(pIdFechaAsist);
	  set pTipoTurno = -1;
	  set Mensaje = '';
	  
	  if(DiaSemana=1)
	  then
		select IdTurnoDom into pTipoTurno
		from tahor_horario 
		where IdTipoHorario = inpTipoHorario
		limit 1;
	  end if;
	  
	  IF(DiaSemana=2)
	  THEN
		SELECT IdTurnoLun INTO pTipoTurno
		FROM tahor_horario 
		WHERE IdTipoHorario = inpTipoHorario
		LIMIT 1;
	  END IF;
	  
	  
	  IF(DiaSemana=3)
	  THEN
		SELECT IdTurnoMar INTO pTipoTurno
		FROM tahor_horario 
		WHERE IdTipoHorario = inpTipoHorario
		LIMIT 1;
	  END IF;
	  
	  IF(DiaSemana=4)
	  THEN
		SELECT IdTurnoMie INTO pTipoTurno
		FROM tahor_horario 
		WHERE IdTipoHorario = inpTipoHorario
		LIMIT 1;
	  END IF;
	  
	  IF(DiaSemana=5)
	  THEN
		SELECT IdTurnoJue INTO pTipoTurno
		FROM tahor_horario 
		WHERE IdTipoHorario = inpTipoHorario
		LIMIT 1;
	  END IF;
	  
	  IF(DiaSemana=6)
	  THEN
		SELECT IdTurnoVie INTO pTipoTurno
		FROM tahor_horario 
		WHERE IdTipoHorario = inpTipoHorario
		LIMIT 1;
	  END IF;
	  
	  IF(DiaSemana=7)
	  THEN
		SELECT IdTurnoSab INTO pTipoTurno
		FROM tahor_horario 
		WHERE IdTipoHorario = inpTipoHorario
		LIMIT 1;
	  END IF;
  
  	
	if(pTipoTurno!=-1)
	then
		if(pTipoTurno=0)
		then
			set Mensaje = 'DIA LIBRE O DESCANSO';
		end if;
	end if;
  	
  return Mensaje;
  
  END */$$
DELIMITER ;

