DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `nuevo_ListarLicencias`(pFechaConsulta DATE, pDocumentoDNI INT(8), pTipo char(1))
BEGIN
	
	
	DECLARE pObservacion CHAR(250);
	
  
/*declaramos la variable para obtener el nombre de la licencia*/
DECLARE pTipoLicencia CHAR(255);
/*declaramos la variable para guardar el gose de haber*/
DECLARE pConGoce int(1);
/*DECLARAMOS ESTA VARIABLE PARA ALMACENAR los nombres*/
DECLARE pConGoceNombre CHAR(20);
/*DECLARAMOS ESTA VARIABLE PARA ALMACENAR los nombres*/
DECLARE pNombres char(255);
/*DECLARAMOS ESTA VARIABLE PARA definir la hora de inicio*/
DECLARE pHoraInicio TIME;
/*DECLARAMOS ESTA VARIABLE PARA definir la hora de finalizar el permiso*/
DECLARE pHoraFin time;
/*DECLARAMOS ESTA VARIABLE PARA mostar la resta del tiempo de permiso*/
DECLARE pTotalHoras time;  
  
  
		/*--------------------CONSULTA PARA OBTEBER EL NOMBRE COMPLETO DEL PERSONAL--------------*/
	SELECT CONCAT(Paterno,' ',Materno,', ',Nombres) INTO pNombres
	FROM tapri_persona WHERE DocumentoDNI=pDocumentoDNI
	LIMIT 1;	
	/*------------------------*/
	
	
	/*aqui obtenemos los nombres de las licencias*/
	sELECT NombrePermiso INTO pTipoLicencia
	FROM tadia_tiposdepermisos
	WHERE IdTipoPermiso IN (SELECT IdTipoPermiso FROM tareg_registropermisopersonal WHERE DocumentoDNI = pDocumentoDNI AND FechaPermiso = pFechaConsulta)
	LIMIT 1;
	
	
	/*---------------AQUI FILTRAMOS SI EXISTE O NO PERMISO---------------*/
	IF(ISNULL(pTipoLicencia)=1)/*si es null permiso= true=1*/
	THEN
		SET pTipoLicencia = '';		
	ELSE 
		SELECT conGoceHaber INTO pConGoce
		FROM tadia_tiposdepermisos WHERE NombrePermiso=pTipoLicencia
		LIMIT 1;
		
		if(pConGoce=1)/*-----SI HAY PERMISO DEFINIMOS SI ES CON GOCE O NO -----------*/
		then 
			set pConGoceNombre="CON GOCE DE HABER";
		ELSE
			SET pConGoceNombre="SIN GOCE DE HABER";
		END IF;
	END IF;
	
	
	/*-------------------AQUI DERFINIMOS LAS HORAS DE PERMISO-------------*/
	IF(pTipo='L')
	then 
		set pHoraInicio="";
		set pHoraFin="";
		set pTotalHoras="TODO EL DIA";
	END IF;
	
	IF(pTipo='P')
	then
		/*----------para la hora inicio----------*/
		SELECT HoraInicio into pHoraInicio
		FROM tareg_registropermisopersonal 
		WHERE DocumentoDNI = pDocumentoDNI AND FechaPermiso = pFechaConsulta 
		LIMIT 1;
		/*-------para la hora fin ----------*/
		SELECT HoraFin into pHoraFin
		FROM tareg_registropermisopersonal 
		WHERE DocumentoDNI = pDocumentoDNI AND FechaPermiso = pFechaConsulta 
		LIMIT 1;
		/*----------para determianr las horas-------*/
		SELECT SEC_TO_TIME(TIME_TO_SEC(HoraFin)-TIME_TO_SEC(HoraInicio)) into pTotalHoras
		FROM tareg_registropermisopersonal 
		WHERE DocumentoDNI = pDocumentoDNI AND FechaPermiso = pFechaConsulta 
		LIMIT 1;
		
	end if;
			
/*define el reporte*/	
	  
	SELECT CAST(CONCAT(DAY(pFechaConsulta),'/',MONTH(pFechaConsulta),'/',YEAR(pFechaConsulta)) AS CHAR(15)),pDocumentoDNI,pNombres,pTipoLicencia,pConGoceNombre,Documento,
	pHoraInicio,pHoraFin,pTotalHoras
	FROM tareg_registropermisopersonal 
	WHERE DocumentoDNI = pDocumentoDNI AND FechaPermiso = pFechaConsulta AND tipo=pTipo
	LIMIT 1;
    
    END */$$
DELIMITER ;

