/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `nuevo_reporte`(pFechaConsulta DATE, pIdArea INT(10),pIdCargo INT(10),pDocumentoDNI INT(8),pHabilitado INT(1),pAreaModalidad char(100))
BEGIN
	
	DECLARE pContador INT(5);
	DECLARE pHoraInicio TIME;
	DECLARE pHoraFin TIME;
	DECLARE pEntradaUno CHAR(10);
	DECLARE pSalidaUno CHAR(10);
	DECLARE pEntradaSisUno CHAR(10);
	DECLARE pSalidaSisUno CHAR(10);
	DECLARE pTardanzaUno CHAR(10);
	DECLARE pExtraUno CHAR(10);
/*AGREGAMOS OTROS PARAMETROS O VARIABLES PARA ALMACENAR LA INFORMACION DE LOS PERMISOS*/
	DECLARE pHoraInicioPermiso TIME;
	DECLARE pHoraFinPermiso TIME;
	DECLARE pTotalHorasPermiso TIME;
	DECLARE pLicencia CHAR(255);
	DECLARE pIndice INT(3);
	DECLARE pPermiso CHAR(250);
	DECLARE pObservacion CHAR(250);
	DECLARE EsDiaLibre CHAR(250);
	DECLARE perteneceModalidad INT(2);
	DECLARE pHayVacacion INT;
	DECLARE pDocVacacion  char(200);
	
	DECLARE pHorasTrabajadas TIME;
	DECLARE pHorasExtras TIME;
	DECLARE pSegundosTrabajados INT;
	DECLARE pSegundosTardanza INT;
	DECLARE pTotalTardanzas CHAR(10);
	
/*DECLARAMOS ESTA VARIABLE PARA ALMACENAR EL VALOR DE ID HORARIO PARA DETERMINAR EL TIPO DE HORARIO DE LA PERSONA*/
DECLARE horario INT(10);
/*declaramos  esta variable para REDONDEAR los segundos*/
	DECLARE segundosTardanza1 INT(10);
	DECLARE segundosTardanza2 INT(10);
	DECLARE segundosTotal INT(10);
	DECLARE segondosHotasTrabajadas INT(10);
	DECLARE segundosExtras INT(10);
/*FIN*/
	
/*declaramos la variables para la tolerancia*/	
	DECLARE pIntervalo1 TIME;
	DECLARE pIntervalo2 TIME;
	DECLARE pIntervalo3 TIME;
	DECLARE pTolerancia TIME;
/*fin tolerancia*/	
/*DECLARAMOS ESTAS VARIABLES PARA COMPARAR LOS HORARIOS DE INGRESO Y SALIDA PARA LOS TURNOS NOCTURNOS*/	
	DECLARE pEntrada TIME;
	DECLARE pSalida TIME;
/*fin tolerancia*/	
/*DECLARAMOS LA VARIABLE CONTOMISION PARA DETERMINAR LAS OMISIONES DEL PERSONAL*/
	DECLARE contOmision INT(1);
	DECLARE pTipoOmision CHAR(200);
/*FIN CONT OMISION*/
	DECLARE numRegistros INT(2);
	DECLARE diaFeriado CHAR(250);
	DECLARE pDocumento CHAR(250);/*para los permisos y licencias*/
	DECLARE pDocument CHAR(250);/*para las omisiones*/
	/*PARA EL GOCE DE HABER*/
	DECLARE pGoce INT(1);
	DECLARE pGoceNombre CHAR(20);
	/*FIN GOVE DE HABER*/
	
	/*PARA ALMACENAR LOS FILTRADOS DE LOS DIAS FERIADOS*/
	DECLARE pFeriadoo INT(1);
	/*FIN DIAS FERIADOS*/
  
  
	/*PARA ALMACENAR el estado*/
	DECLARE estado INT(1);
	DECLARE pEstado INT(1);
	/*FIN ESTADO*/
  
  	/*PARA VERIFICAR HABILITACION*/
	DECLARE pActivo INT(1);
	DECLARE pExistencia INT(1);
	/*FIN HABILITACION*/
/*PROIMERO VERIFICAMOS SI EXISTE EL TRABAJADOR--- PARA DETERMINAR NOMBRADOS----**/  
  SELECT COUNT(idHabilitacion) INTO pExistencia FROM contrato_nuevopersonal WHERE DocumentoDNI=pDocumentoDNI;
IF(pExistencia=0)/*NO EXISTE---> DEBE SER NOMBRADO*/
THEN
	SET pActivo = 0;
ELSE 
	/*AQUI EVALUAMOS LA FECHA DE INICIO DEL TRABAJADOR*/
	SELECT idHabilitacion INTO pActivo FROM contrato_nuevopersonal WHERE DocumentoDNI=pDocumentoDNI;
END IF;  
  
IF(pActivo<=0)
THEN  
  
  
/*PARA FINTRAR EL ESTA DEL PERSONAL*/
  
  
  
	/*AQUI DETERMINAMOS EL NUMERO DE REGISTROS DE LA PERSONA EN LA FECHA INDICADA*/
	SELECT COUNT(*) INTO pContador FROM tareg_registroasistencia 
	WHERE DocumentoDNI = pDocumentoDNI AND IdFechaAsistencia = pFechaConsulta;
	
	
	/*obtenemos el registro del personal entra y salida por fecha determinadad*/
	SELECT HoraIngreso,HoraSalida INTO pEntrada, pSalida FROM tareg_registroasistencia 
	WHERE DocumentoDNI = pDocumentoDNI AND IdFechaAsistencia = pFechaConsulta LIMIT 1;
	/*/se hace ESTE ANALISIS PARA EVITAR EL NULO/*/
	IF(ISNULL(pEntrada)=1 AND ISNULL(pSalida)=1)
	THEN
		SET pEntrada='00:00:00';
		SET pSalida='00:00:00';
	END IF;
	
	/*DETERMINAR EL NUMERO DE REGIOSTROS POR DIA DE LA PERSONA*/
	SET numRegistros=0;
	IF(TIME_TO_SEC(pEntrada)>0)
	THEN
		SET numRegistros=numRegistros+1;
	END IF;
	IF(TIME_TO_SEC(pSalida)>0)
	THEN
		SET numRegistros=numRegistros+1;
	END IF;
	
	/*EN ESTA PARTE CONSULTAMOS SI EXISTE PERMISOS, OBTENIENDO SU NOMBRE*/
		
	/*AQUI GUARDAMOS EL NOMBRE DE PERMISO HORA DE INICIO Y FIN DEL PERMISO SI ES QUE HAY PERMISO POR HORAS*/
	SELECT CAST(PER.NombrePermiso AS CHAR(200)),CAST(REG.HoraInicio AS TIME),CAST(REG.HoraFin AS TIME),CAST(REG.Documento AS CHAR(200)),PER.conGoceHaber INTO pPermiso, pHoraInicioPermiso, pHoraFinPermiso,pDocumento,pGoce
	FROM ((SELECT * FROM tareg_registropermisopersonal  WHERE DocumentoDNI=pDocumentoDNI AND FechaPermiso = pFechaConsulta AND tipo="P") REG 
	INNER JOIN  tadia_tiposdepermisos PER ON PER.IdTipoPermiso = REG.IdTipoPermiso)
	LIMIT 1;
		
	
	/*AQUI GUARDAMOS EL NOMBRE DE LICENCIAS SI HAY LICENCIAS*/
	SELECT PER.NombrePermiso, REG.Documento,PER.conGoceHaber INTO pLicencia, pDocumento, pGoce
	FROM ((SELECT * FROM tareg_registropermisopersonal  WHERE DocumentoDNI=pDocumentoDNI AND FechaPermiso = pFechaConsulta AND tipo="L") REG 
	INNER JOIN  tadia_tiposdepermisos PER ON PER.IdTipoPermiso = REG.IdTipoPermiso)
	LIMIT 1;
	
	/*DETERMINACION SI EL PERMISO O LICENCIA ES CON GOCE O SIN GOCE DE HABER*/
	IF(ISNULL(pGoce)=1)/*si es null LICENCIAS= true=1*/
