		SET pGoce = '';		
	ELSE 
		IF(pGoce=0)
		THEN
			SET pGoceNombre='S/GOCE';
		ELSE 
			SET pGoceNombre='C/GOCE';
		END IF;
	END IF;
	/*FIN GOCE*/
	 
	/*guardamos el nombre del dia FERIADO en la tabla dias no laborables= en diaFeriado----------horario estatico */
	SELECT DF.NombreFeriado INTO pObservacion
	FROM ((SELECT * FROM tahor_diasnolaborables WHERE IdDiaNoLaborable=pFechaConsulta) DNL INNER JOIN tadia_tiposdiasferiados DF ON DF.IdTipoDiaFeriado = DNL.IdTipoDiaFeriado)
	LIMIT 1;
	
	/*DETERMINAMOS EL DIA LIBRE O DESCANSO PARA HORARIOS ESTATICOS*/
	SELECT fnRep_RecuperarTurnoLibre(pDocumentoDNI,pFechaConsulta) INTO EsDiaLibre;		
	
