	SELECT Documento INTO pDocVacacion
	FROM tareg_vacacionespersonal
	WHERE DocumentoDNI = pDocumentoDNI AND FechaVacacion = pFechaConsulta;
	
	/*----------------FIN VACACIONES---------------*/
	
	
