	SELECT tolerancia,intervalo1,intervalo2,intervalo3 INTO pTolerancia,pIntervalo1,pIntervalo2,pIntervalo3  FROM tnuevo_tolerancia;
	/*----------FIN TOLERANCIA------------*/
	
	
	/*-------AQUI DETERMINAMOS SI EL PERSONAL TUBO O NO OMISION DE REGISTRO-------------*/
	SELECT COUNT(*) INTO contOmision FROM tnuevoomision WHERE DocumentoDNI=pDocumentoDNI AND fechaOmision = pFechaConsulta;
	SELECT tipoOmision,documento INTO  pTipoOmision, pDocument FROM tnuevoomision WHERE DocumentoDNI=pDocumentoDNI AND fechaOmision = pFechaConsulta;  
	/*/se hace ESTE ANALISIS PARA EVITAR LOS NULO/*/
	IF(ISNULL(pTipoOmision)=1 AND ISNULL(pDocument)=1)
	THEN
		SET pTipoOmision='';
		SET pDocument='';
	END IF;
	
	/*----------FIN OMISION------------*/
	
	
	
	
	/*EVALUAMOS LAS OBSERVACIONES */
	/*EVALUAMOS SI LA LICENCIA ES NULA O NO*/
	IF(ISNULL(pLicencia)=1)/*si es null LICENCIAS= true=1*/
	THEN
		SET pLicencia = '';/*SI ES NULA LE ASIGNAMOS VALOR VACIO*/		
		IF(pContador<=0)
		THEN
			SET pLicencia = 'FALTA';
		END IF;
	END IF;
	IF(ISNULL(EsDiaLibre)=1)/*SI EsDiaLibre ES NULO = true = 1= VACIO*/
	THEN
		SET EsDiaLibre='';
	END IF;
	
	
	/*PARA LOS ESTADOS*/
	SELECT EnableSINO INTO estado FROM tapri_contratopersonal WHERE DocumentoDNI=pDocumentoDNI; 
	/*FIN ESTADOS*/
	
	IF(ISNULL(pObservacion)=1)/*SI POBSERVACION ES NULO = true = 1= VACIO*/
	THEN
		SET pObservacion = '';		
		IF(pLicencia='FALTA')/*aqui preguntamos si pPermiso es falta de la configuracion anterior*/
		THEN
			IF(EsDiaLibre='')
			THEN
				SET pLicencia = '';
				IF(estado=1)
				THEN
