		SET pObservacion = CONCAT('VACACIONES -',pDocVacacion);
	END IF;
	/*FIN OBSERVACIONES*/
	
	
		/*FILTRADO FINAL PARA DETERMINAR Y EVITAR LOS FERIADOS DOMINICALES*/
		SELECT COUNT(*) INTO pFeriadoo FROM tadia_tiposdiasferiados WHERE NombreFeriado=pObservacion;
		/*FIN FILTRADO*/
	
		
/*INICIO DE LA CONSICION SI EXISTE O NO REGISTROS*/
IF(pContador>0)/*SI ES MAYOR QUE CERO ==> HAY MAS DE UN REGISTRO*/
THEN
			
	SELECT CAST(HoraIngreso AS CHAR(10)),CAST(HoraSalida AS CHAR(10)),CAST(HoraIngresoSist AS CHAR(10)),CAST(HoraSalidaSist AS CHAR(10))  
	INTO  pEntradaUno,pSalidaUno,pEntradaSisUno,pSalidaSisUno
	FROM tareg_registroasistencia
	WHERE DocumentoDNI = pDocumentoDNI AND IdFechaAsistencia = pFechaConsulta  
	ORDER BY HoraIngreso ASC
	LIMIT 1;
	/*EVALUAMOS LOS NULOS*/
	IF(ISNULL(pEntradaUno)=1 AND ISNULL(pSalidaUno)=1 AND ISNULL(pEntradaSisUno)=1 AND ISNULL(pSalidaSisUno)=1)
	THEN
		SET pEntradaUno='00:00:00';
		SET pSalidaUno='00:00:00';
		SET pEntradaSisUno='00:00:00';
		SET pSalidaSisUno='00:00:00';
	END IF;	
	
	/*EVALUAMOS LA CONDICION SI TIENE O NO REGISTROSD*/
	IF(numRegistros=1)/*/SI SOLO TIENE UNA PICADA EN LA MAÑANA O TARDE*/
	THEN
		/*EVALUAMOS LAS CONSICIONES SI ENTRA EN LA MAÑANA U SOLO TIENE UNA PICADA*/
		IF(TIME_TO_SEC(pEntradaUno)<TIME_TO_SEC('13:00:00'))
		THEN
			/*---------PARA LA FALTA POR TARDANZA CUANDO EL ACCESO ES MAS DE LA HORA PERMITIDA------------*/
			IF((TIME_TO_SEC(pEntradaSisUno)+TIME_TO_SEC(pTolerancia)+TIME_TO_SEC(pIntervalo1)+TIME_TO_SEC(pIntervalo2)+TIME_TO_SEC(pIntervalo3))<TIME_TO_SEC(pEntradaUno))
			THEN
				SET pTardanzaUno = '00:00:00';
				/*FILTRADO FINAL PARA DETERMINAR Y EVITAR LOS FERIADOS DOMINICALES*/
				IF(pFeriadoo > 0)
				THEN
					SET pObservacion = CONCAT(pObservacion,' - APLICABLE SOLO PARA LAS HORAS EXTRAS');
				ELSE 
					SET pObservacion = 'FALTA POR TARDANZA - SUJETO A DESCUENTO';
				END IF;
				/*FIN FILTRADO*/
								
			ELSE /*CUANDO NO HAY FALTA POR TARDANZA HAY PERMISO OMISION , FERIADO MEDIO DIA*/
				
				IF(pObservacion='')
				THEN
					IF(pTipoOmision!='')
					THEN
						SET pObservacion=CONCAT('OMISION DE REGISTRO (',pTipoOmision,') - ',pDocument);
					ELSE
						/*EVALUAMOS SI HAY PERMISO*/
						IF(ISNULL(pPermiso)=1)/*si es null LICENCIAS= true=1*/
						THEN
							SET pObservacion='REGISTRO IRREGULAR - SUJETO A DESCUENTO';		
						ELSE 
							SET pObservacion=CONCAT(pGoce,'',pPermiso,' - ',pGoceNombre,'- ',pDocumento);
						END IF;	/*FIN PERMISOS*/
					
					END IF;				
				ELSE 
					SET pObservacion=pObservacion;
				END IF;
				
				IF(TIME_TO_SEC(pEntradaSisUno)<TIME_TO_SEC(pEntradaUno))
				THEN
					SET pTardanzaUno = SEC_TO_TIME(TIME_TO_SEC(pEntradaUno)-TIME_TO_SEC(pEntradaSisUno));
				ELSE 
					SET pTardanzaUno ='00:00:00';
				END IF;
			END IF;/*FIN FALTA POR TARDANZA*/
		
		END IF;
		/*PARA DETERMINAR LAS POSIBLES SUACIONES SI TIENE UNA ENTRADA SOLO EN LA TARDE*/
		IF(TIME_TO_SEC(pEntradaUno)>TIME_TO_SEC('13:00:00'))
		THEN
			IF(pObservacion='')
			THEN
				IF(pTipoOmision!='')
				THEN
					SET pObservacion=CONCAT('OMISION DE REGISTRO (',pTipoOmision,') - ',pDocument);
				ELSE 
					SET pObservacion='FALTA POR TARDANZA - SUJETO A DESCUENTO';
				END IF;
			ELSE 
				SET pObservacion=pObservacion;
				
			END IF;
			
			/*PARA TODOS LOS CASOS DE LA PICADA EN LA TARDE CAMBIAMOS LOS REGISTROS*/
			SET pSalidaUno=pEntradaUno;
			SET pEntradaUno='00:00:00';
		
		END IF;/*/FIN REGISTRO POR LA TARDE*/
	
	END IF;/*/FIN SOLO UN  REGISTRO SEA TARDE O MALÑANA*/
	/*EVALUAMOS LA CONDICION SI TIENE 2 REGISTROS O PICADAS  AL DIA*/
	IF(numRegistros=2)/*A QUI CALCULAMOS LAS TARDANZAS,EXTRAS, HORAS TRABAJADAS, PERSISOS*/
	THEN
		/*PRIMERO EVALUIAMOS SI TIENE FALTA POR TARDANZA*/
		IF((TIME_TO_SEC(pEntradaSisUno)+TIME_TO_SEC(pTolerancia)+TIME_TO_SEC(pIntervalo1)+TIME_TO_SEC(pIntervalo2)+TIME_TO_SEC(pIntervalo3))<TIME_TO_SEC(pEntradaUno))
		THEN
			SET pTardanzaUno = '00:00:00';
			/*FILTRADO FINAL PARA DETERMINAR Y EVITAR LOS FERIADOS DOMINICALES*/
				IF(pFeriadoo > 0)
				THEN
					SET pObservacion = CONCAT(pObservacion,' - APLICABLE SOLO PARA LAS HORAS EXTRAS');
				ELSE 
					SET pObservacion = 'FALTA POR TARDANZA - SUJETO A DESCUENTO';
				END IF;
				/*FIN FILTRADO*/
			ELSE /*CUANDO NO HAY FALTA POR TARDANZA HAY PERMISO OMISION , FERIADO MEDIO DIA*/	
	
			/*EVALUAMOS SI HAY PERMISO*/
			IF(ISNULL(pPermiso)=1)/*si es null LICENCIAS= true=1*/
			THEN
				SET pPermiso = '';		
			ELSE 
				SET pObservacion=CONCAT(pGoce,'',pPermiso,' - ',pGoceNombre,'- ',pDocumento);
			END IF;	/*FIN PERMISOS*/
			/*CALCULAMOS LA TARDANZA*/
			IF(TIME_TO_SEC(pEntradaUno)>TIME_TO_SEC(pEntradaSisUno))
			THEN
				SET pTardanzaUno =SEC_TO_TIME(TIME_TO_SEC(pEntradaUno)-TIME_TO_SEC(pEntradaSisUno));
			END IF;/*/FIN TRADANZA/*/
			/*CALCULAMOS LAS HORAS EXTRAS*/
			IF(TIME_TO_SEC(pSalidaSisUno)<TIME_TO_SEC(pSalidaUno))
			THEN
				SET pExtraUno =SEC_TO_TIME(TIME_TO_SEC(pSalidaUno)-TIME_TO_SEC(pSalidaSisUno));
			END IF;/*/FIN EXTRAS/*/	
			/*CALCULAMOS LAS HORAS TRABAJADAS*/
			IF(TIME_TO_SEC(pSalidaUno)-TIME_TO_SEC(pEntradaUno)>0 )
			THEN
				SET pHorasTrabajadas =SEC_TO_TIME(TIME_TO_SEC(pSalidaUno)-TIME_TO_SEC(pEntradaUno));
			END IF;/*/FIN TRABAJADAS/*/	
		END IF;/*FIN FALTA POR TARDANZA*/	
	END IF;/*/FIN DOS REGISTROS POR DIA/*/
	
ELSE/*EVALUAMOS SI NO HAY REGISTROS ENTONCES DEBE HABER LICENCIAS , FERIADOS, VACACIONES, DIAS LIBRES*/
			SET pObservacion=pObservacion;
			
END IF;/*FIN NUMERO DE REGISTROS*/
	
			
	/*/---------------PARA DEFINIR LA HORA DEL PERMISO--------------/*/
	SET pTotalHorasPermiso = SEC_TO_TIME(TIME_TO_SEC(pHoraFinPermiso)-TIME_TO_SEC(pHoraInicioPermiso));
	/*------ FIN -----------*/
	/*verificamos si la operacion de restar es nula o no*/
	IF(ISNULL(pTotalHorasPermiso)=1)
	THEN 
		SET pTotalHorasPermiso='00:00:00';
		
	END IF;
	/*/------------total de horas trabajadas con disminucion de las tardanzas---------------/*/
	/*SET pSegundosTrabajados = ;*/
	IF(TIME_TO_SEC(pEntradaUno)> 0 AND TIME_TO_SEC(pSalidaUno)>0)/*EVALUAMOS LA ENTRADA Y SALIDA*/
	THEN
		SET pHorasTrabajadas = SEC_TO_TIME(TIME_TO_SEC(pSalidaUno)-TIME_TO_SEC(pEntradaUno)-TIME_TO_SEC(pTotalHorasPermiso));
	ELSE 
		SET pHorasTrabajadas='00:00:00';
	END IF;
	
	
	
	/*evaluamos el estado del personal si esta habilitado o no */
	IF(pHabilitado=0)
	THEN 
		SELECT COUNT(*) INTO pEstado FROM tapri_contratopersonal WHERE DocumentoDNI=pDocumentoDNI AND EnableSINO = 0;
	END IF;
	
	IF(pHabilitado=1)
	THEN 
		SELECT COUNT(*) INTO pEstado FROM tapri_contratopersonal WHERE DocumentoDNI=pDocumentoDNI AND EnableSINO = 1;
	END IF;
	
	IF(pHabilitado=2)
	THEN 
		SELECT COUNT(*) INTO pEstado FROM tapri_contratopersonal WHERE DocumentoDNI=pDocumentoDNI AND (EnableSINO = 0 OR EnableSINO = 1);
	END IF;
	
	
	/*fin estado del personal*/
	
	/*DETERMINAMOS SI ES POR MODALIDAD O POR AREA*/
	IF(pAreaModalidad="MODALIDAD")/*1=MODALIDADDES----> FILTRADO POR MODALIDADES*/
	THEN 
		SELECT fnPerteneceAModalidad(pDocumentoDNI,pIdArea) INTO perteneceModalidad;
	END IF;
	
	IF(pAreaModalidad="AREAS")/*2 = AREA---> FILTRADO POR AREAS*/
	THEN 
		SELECT fnPerteneceArea(pDocumentoDNI,pIdArea) INTO perteneceModalidad;
	END IF;	
	
	/*SELECT fnPerteneceArea(pDocumentoDNI,pIdArea) INTO perteneceModalidad;*/
    
	SELECT CAST(CONCAT(DAY(pFechaConsulta),'/',MONTH(pFechaConsulta),'/',YEAR(pFechaConsulta)) AS CHAR(15)),DocumentoDNI,CAST(CONCAT(Paterno,' ',Materno,', ',Nombres) AS CHAR(150)),pEntradaUno,pTardanzaUno,pSalidaUno,
		pHoraInicioPermiso,pHoraFinPermiso,pTotalHorasPermiso,
		pHorasTrabajadas,pTardanzaUno,pExtraUno,pObservacion,estado
	FROM tapri_persona 
	WHERE DocumentoDNI = pDocumentoDNI AND perteneceModalidad=1 AND pEstado = 1  
	LIMIT 1;
else
/*aqui emitimos los valores en nulo para los deshabilitados*/
SELECT CAST(CONCAT(DAY(pFechaConsulta),'/',MONTH(pFechaConsulta),'/',YEAR(pFechaConsulta)) AS CHAR(15)),DocumentoDNI,CAST(CONCAT(Paterno,' ',Materno,', ',Nombres) AS CHAR(150)),pEntradaUno,pTardanzaUno,pSalidaUno,
		pHoraInicioPermiso,pHoraFinPermiso,pTotalHorasPermiso,
		pHorasTrabajadas,pTardanzaUno,pExtraUno,pObservacion,estado
	FROM tapri_persona 
	WHERE DocumentoDNI = pDocumentoDNI AND perteneceModalidad=1 AND pEstado = 1  
	LIMIT 1;
end if;
	
	
