	SELECT CAST(PER.NombrePermiso AS CHAR(200)),CAST(REG.HoraInicio AS TIME),CAST(REG.HoraFin AS TIME),CAST(REG.Documento AS CHAR(200)),PER.conGoceHaber INTO pPermiso, pHoraInicioPermiso, pHoraFinPermiso,pDocumento,pGoce
	FROM ((SELECT * FROM tareg_registropermisopersonal  WHERE DocumentoDNI=pDocumentoDNI AND FechaPermiso = pFechaConsulta AND tipo="P") REG 
	INNER JOIN  tadia_tiposdepermisos PER ON PER.IdTipoPermiso = REG.IdTipoPermiso)
	LIMIT 1;
		
	
