	SELECT tolerancia,intervalo1,intervalo2,intervalo3 INTO pTolerancia,pIntervalo1,pIntervalo2,pIntervalo3  FROM tnuevo_tolerancia;
	/*----------FIN TOLERANCIA------------*/
	
	
