/*!50003 DROP PROCEDURE IF EXISTS  `spuHor_GuardarDiasVacacionEmpleado` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `spuHor_GuardarDiasVacacionEmpleado`( pDNI char(12),pDiaMesAnio varchar(300), pIdTipoVacacion int(10),pDocumento char(100))
BEGIN
    
    declare pTrabaja int(4);
    declare nroVacacionesTomadas int(3);
    DECLARE nroVacaciones INT(3);
    DECLARE nuevoVacaciones INT(3);
    
    
    /*aqui determinamos el numero total de vacaciones del trabajador*/
    SELECT numeroVacaciones into nroVacaciones FROM tb_vacaciones WHERE DocumentoDNI=pDNI;
        
    /*DETERMINAMOS EL NUMERO DE VACACIONES AGREGADOS POR EL CALENDARIO*/
    SELECT COUNT(*) INTO nroVacacionesTomadas FROM tareg_vacacionespersonal WHERE DocumentoDNI=pDNI;        
        
    /*AQUI VERIFICAMOS SI ESE DIA O FECHA YA TIENE VACACIO0NES O NO*/
    SELECT COUNT(*) INTO pTrabaja FROM tareg_vacacionespersonal WHERE DocumentoDNI = pDNI AND FechaVacacion = pDiaMesAnio;
    /*SI pTrabaja = 0 ----> NO TIENE VACACIONES*/
    /*SI pTrabaja = 1 ----> SI TIENE VACACIONES*/
     
     
    if(nroVacaciones >= nroVacacionesTomadas)
    then 
	if(pIdTipoVacacion=0 and pTrabaja=1)
	then
		SET nuevoVacaciones =nroVacacionesTomadas-1;
		UPDATE tb_vacaciones SET vacaAsignado=nuevoVacaciones WHERE DocumentoDNI=pDNI;
		/*BORRAMOS LAS VACACIONES ASIGNADAS ESE DIA*/
		delete from tareg_vacacionespersonal where DocumentoDNI=pDNI and FechaVacacion = pDiaMesAnio;
	
	end if;
	
	IF(pIdTipoVacacion=1 AND pTrabaja=0)
	THEN
		SET nuevoVacaciones = nroVacacionesTomadas+1;
		UPDATE tb_vacaciones SET vacaAsignado=nuevoVacaciones WHERE DocumentoDNI=pDNI;
		/*INSERTAMOS EL DIA CON VACACIONES ASIGNADO*/
		INSERT INTO tareg_vacacionespersonal VALUES(pDNI,pDiaMesAnio,pDocumento);
	
	END IF;	
    
    end if; 
     
     
END */$$
DELIMITER ;

