	declare pExistePermiso int(10);
	declare pHoraInicioPermiso time;
	
	select count(*) into pExiste from tapri_contratopersonal where DocumentoDNI = pDNI;
	
	if(pExiste>0)
	then
	
		set i_DiaSemana = dayofweek(CURRENT_DATE); 
		
		
		
		
		
		
		
		
		
		
		select IdTipoHorario into pIdHorarioEmpleado FROM tapri_contratopersonal WHERE DocumentoDNI = pDNI limit 1;
		
		
		CASE (i_DiaSemana)	
		WHEN 1 THEN select IdTurnoDom into pIdTurnoSemana from tahor_horario where IdTipoHorario = pIdHorarioEmpleado limit 1;
		WHEN 2 THEN SELECT IdTurnoLun INTO pIdTurnoSemana FROM tahor_horario WHERE IdTipoHorario = pIdHorarioEmpleado LIMIT 1;
