		WHEN 4 THEN SELECT IdTurnoMie INTO pIdTurnoSemana FROM tahor_horario WHERE IdTipoHorario = pIdHorarioEmpleado LIMIT 1;
		WHEN 5 THEN SELECT IdTurnoJue INTO pIdTurnoSemana FROM tahor_horario WHERE IdTipoHorario = pIdHorarioEmpleado LIMIT 1;
		WHEN 6 THEN SELECT IdTurnoVie INTO pIdTurnoSemana FROM tahor_horario WHERE IdTipoHorario = pIdHorarioEmpleado LIMIT 1;
		WHEN 7 THEN SELECT IdTurnoSab INTO pIdTurnoSemana FROM tahor_horario WHERE IdTipoHorario = pIdHorarioEmpleado LIMIT 1;
