		from tahor_turnos 
		where IdTurno = pIdTurnoSemana and HoraEntrada<=current_time and current_time<=HoraSalida
		limit 1;
		
		if(ISNULL(pIdSubTurnoSemana)=1)
		then
			
			SELECT IdSubturno INTO pIdSubTurnoSemana
			FROM tahor_turnos 
			WHERE IdTurno = pIdTurnoSemana AND CURRENT_TIME<=HoraEntrada
			order by HoraEntrada asc
			LIMIT 1;
		end if;
		
		IF(ISNULL(pIdSubTurnoSemana)=1)
		THEN
			
			
			
			SET pIdSubTurnoSemana = -1;
		END IF;
		
			
		if(pIdSubTurnoSemana>=1)
		then 
			
			
			
			
			
			
			
			
			SELECT HoraEntrada,HoraSalida INTO pHoraEntradaSist,pHoraSalidaSist
			FROM tahor_turnos 
			WHERE IdTurno = pIdTurnoSemana AND IdSubturno=pIdSubTurnoSemana
