		end if;
		
		
		
		select count(*) into pNroRegistrosHoy
		from tareg_registroasistencia
		where DocumentoDNI = pDNI and IdFechaAsistencia = current_date and RegistroSalida = 0;		
		
		if(pNroRegistrosHoy>0)
