			RegistroSalida = 1
			where DocumentoDNI = pDNI AND IdFechaAsistencia = CURRENT_DATE and HoraIngreso = pHoraEntradaAnterior;			
			
			
			SET pMensaje = "HASTA LUEGO : REGISTRO REALIZADO CON EXITO";
			
		else
			
			insert into tareg_registroasistencia values(pDNI,current_date,current_time,current_time,0,pHoraEntradaSist,pHoraSalidaSist,pIdHorarioEmpleado,pIdTurnoSemana,pIdSubTurnoSemana);
			
			
			
			
			
			
			
			
			
			
			
			
			
			select count(*) into pExistePermiso
			from tareg_registropermisopersonal
			where DocumentoDNI = pDNI and FechaPermiso = CURRENT_DATE and HoraInicio<=current_time
			and UsoPermiso = 0;
			
			if(pExistePermiso>0)
			then
				
				SELECT HoraInicio INTO pHoraInicioPermiso
				FROM tareg_registropermisopersonal
				WHERE DocumentoDNI = pDNI AND FechaPermiso = CURRENT_DATE AND HoraInicio<=CURRENT_TIME
				and UsoPermiso=0
				order by HoraInicio asc
				limit 1;
				
				
				update tareg_registropermisopersonal
				set HoraFin = current_time,
				TodoElDia = 0,
				UsoPermiso = 1
				where DocumentoDNI = pDNI AND FechaPermiso = CURRENT_DATE AND HoraInicio=pHoraInicioPermiso;				
				
			end if;				
			
			SET pMensaje = "BIENVENIDO : REGISTRO REALIZADO CON EXITO";
			
		end if;
		
		
		
	else
		
		SET pMensaje = "ATENCION : ESTE USUARIO NO EXISTE EN LA BASE DE DATOS DEL SISTEMA";
	end if;    
	
	select pMensaje;
    
    END */$$
