/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `spuRep_ReporteDiario`(pFechaConsulta DATE, pIdArea INT(10),pIdCargo INT(10),pDocumentoDNI INT(8))
BEGIN
	
	DECLARE pContador INT(5);
	DECLARE pHoraInicio TIME;
	DECLARE pHoraFin TIME;
	DECLARE pEntradaUno CHAR(10);
	DECLARE pEntradaSisUno CHAR(10);
	DECLARE pTardanzaUno CHAR(10);
	
	DECLARE pIndice INT(3);
	DECLARE pPermiso CHAR(250);
	DECLARE pObservacion CHAR(250);
	DECLARE EsDiaLibre CHAR(250);
	DECLARE perteneceModalidad INT(2);
	DECLARE pHayVacacion INT;
	
	
	
	/*declaramos  esta variable para REDONDEAR los segundos*/
	DECLARE segundosTardanza1 INT(10);
	/*FIN*/	
	
/*declaramos la variables para la tolerancia*/	
	DECLARE pTardanza TIME;
	DECLARE pTolerancia TIME;
/*fin tolerancia*/	
	
	
	
	
	/*determina el numero de registros por dia */  
	SELECT COUNT(*) INTO pContador FROM tareg_registroasistencia 
	WHERE DocumentoDNI = pDocumentoDNI AND IdFechaAsistencia = pFechaConsulta;
	
	/*Busca permiso de la persona*/
	SELECT NombrePermiso INTO pPermiso
	FROM tadia_tiposdepermisos
	WHERE IdTipoPermiso IN (SELECT IdTipoPermiso FROM tareg_registropermisopersonal WHERE DocumentoDNI = pDocumentoDNI AND FechaPermiso = pFechaConsulta)
	LIMIT 1;
	/*determina falta del trabajador*/
	IF(ISNULL(pPermiso)=1)
	THEN
		SET pPermiso = '';		
		IF(pContador<=0)
		THEN
			SET pPermiso = 'FALTA';
		END IF;
	END IF;
	
	/*Define los dias mno laborables*/
	SELECT DF.NombreFeriado INTO pObservacion
	FROM ((SELECT * FROM tahor_diasnolaborables WHERE IdDiaNoLaborable=pFechaConsulta) DNL INNER JOIN tadia_tiposdiasferiados DF ON DF.IdTipoDiaFeriado = DNL.IdTipoDiaFeriado)
	LIMIT 1;
	
	
	SELECT fnRep_RecuperarTurnoLibre(pDocumentoDNI,pFechaConsulta) INTO EsDiaLibre;
	
	iF(ISNULL(pObservacion)=1)
	THEN
		SET pObservacion = '';		
		IF(pPermiso='FALTA')
		THEN
			IF(EsDiaLibre='')
			THEN
				SET pPermiso = '';
				SET pObservacion = 'FALTA NO JUSTIFICADA';
			ELSE
				SET pPermiso = '';
				SET pObservacion = EsDiaLibre;
			END IF;
		END IF;
	END IF;
	
	IF(pObservacion!='')
	THEN
		SET pPermiso = '';
	END IF;
		
	IF(pPermiso !='')
	THEN
		SET pObservacion = CONCAT(pObservacion,' - ',pPermiso); 
	ELSE
		SET pObservacion = CONCAT(pObservacion,pPermiso); 
	END IF;
	
	SELECT COUNT(*) INTO pHayVacacion
	FROM tareg_vacacionespersonal
	WHERE DocumentoDNI = pDocumentoDNI AND FechaVacacion = pFechaConsulta;
	
	IF(pHayVacacion>0)
	THEN
		SET pObservacion = 'VACACIONES';
	END IF;
	
	SET pEntradaUno = '00:00:00';
	SET pEntradaSisUno = '00:00:00';
	SET pTardanzaUno = '00:00:00';
	
	
	SET pIndice = 1;
	SET pHoraInicio = CURRENT_TIME;
	SET pHoraFin = CURRENT_TIME;
	
	/*-------AQUI OBTENEMOS LOS MINUTOS DE TOLERANCIA Y TARDANZA-------------*/
	SELECT tolerancia, tardanza INTO pTolerancia,pTardanza  FROM tnuevo_tolerancia;
	/*----------FIN TOLERANCIA------------*/
	
	IF(pContador>0)
	THEN
			
		SELECT CAST(HoraIngreso AS CHAR(10)),CAST(HoraIngresoSist AS CHAR(10))  
		INTO  pEntradaUno,pEntradaSisUno
		FROM tareg_registroasistencia
		WHERE DocumentoDNI = pDocumentoDNI AND IdFechaAsistencia = pFechaConsulta
		ORDER BY HoraIngreso ASC
		LIMIT 1;		
		
		
		iF(TIME_TO_SEC(pEntradaUno)>TIME_TO_SEC(pEntradaSisUno))
		THEN 
			SET pTardanzaUno = CAST(SEC_TO_TIME(TIME_TO_SEC(pEntradaUno)- (TIME_TO_SEC(pEntradaSisUno)+TIME_TO_SEC(pTolerancia))) AS CHAR(10));
		END IF;
		
		/*---------PARA LA FALTA POR TARDANZA------------*/
		IF((TIME_TO_SEC(pEntradaSisUno)+TIME_TO_SEC(pTolerancia)+TIME_TO_SEC(pTardanza))<TIME_TO_SEC(pEntradaUno))
		THEN
			SET pObservacion= CONCAT(pObservacion ,' - ',"FALTA POR TARDANZA");
		END IF;
			
		
		
	END IF;
	
	
	
	
	
		/*PARA REDONDEAR LAS TARDANZAS*/
	
	IF((TIME_TO_SEC( CAST(pTardanzaUno AS TIME)) MOD 60) > 0)
	THEN 
		SET segundosTardanza1 = TIME_TO_SEC(CAST(pTardanzaUno AS TIME))MOD 60;
		SET  pTardanzaUno = CAST(SEC_TO_TIME((TIME_TO_SEC(pTardanzaUno) - segundosTardanza1)+60)AS CHAR(10));        
	END IF;
	/*FIN REDONDEA LAS TARDANZAS*/
	
	
	
	
	/*SELECT fnPerteneceAModalidad(pDocumentoDNI,pIdArea) INTO perteneceModalidad;*/
    
	SELECT CAST(CONCAT(DAY(pFechaConsulta),'/',MONTH(pFechaConsulta),'/',YEAR(pFechaConsulta)) AS CHAR(15)),DocumentoDNI,CAST(CONCAT(Paterno,' ',Materno,', ',Nombres) AS CHAR(150)),pEntradaUno,pTardanzaUno,pObservacion
	FROM tapri_persona 
	WHERE DocumentoDNI = pDocumentoDNI 
	LIMIT 1;
    
    END */$$
DELIMITER ;

