/* Procedure structure for procedure `spuRep_ReporteGeneralResumido` */

/*!50003 DROP PROCEDURE IF EXISTS  `spuRep_ReporteGeneralResumido` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `spuRep_ReporteGeneralResumido`(pFechaDesde Date,pFechaHasta date, pIdAgencia int(10),pIdArea int(10),pIdCargo int(10),pDocumentoDNI int(8))
BEGIN
    
	select A.NombreArea,
	CONCAT(P.Paterno,' ',P.Materno,' ',P.Nombres) as 'NOMBRE',
	C.DocumentoDNI,
	DAY(C.IdFechaAsistencia) as 'DIA',
	MONTH(C.IdFechaAsistencia) as 'MES', 
	year(C.IdFechaAsistencia) as 'ANIO',
	HoraIngreso,
	HoraSalida,
	subtime(HoraSalida,HoraIngreso)	as 'HORAS TRABAJADAS',
	fnRep_BuscarPermisos(C.DocumentoDNI,C.IdFechaAsistencia) as 'PERMISOS'
	from (select * from tareg_registroasistencia where (DocumentoDNI = pDocumentoDNI or pDocumentoDNI='NONE') and IdFechaAsistencia >= pFechaDesde and IdFechaAsistencia <=pFechaHasta ) C
	inner join ((tapri_persona P inner join (select * from tapri_contratopersonal 
	where (IdAgencia = pIdAgencia or pIdAgencia='0') and (IdArea=pIdArea or pIdArea=0) and(IdCargo = pIdCargo or pIdCargo=0)
	) CT on P.DOcumentoDNI = CT.DocumentoDNI) inner join taofi_areas A on CT.IdArea = A.IdArea ) on C.DocumentoDNI = P.DocumentoDNI
	order by C.IdFechaAsistencia asc,P.Paterno,P.Materno,P.Nombres;
    
    END */$$
DELIMITER ;

